    const onToken = token => {
        console.log(token);
        alert('Payment Succesful');
    }

    return (
      <StripeCheckout
        label="Pay Now"
        name="Helio"
        billingAddress
        shippingAddress
        image=""
        description={`Your total is  $${price}`}
        amount={priceForStripe}
        panelLabel='Pay Now'
        token={onToken}
        stripeKey={publishableKey}
      />
    );
}

export default StripeCheckoutButton;
